$CorsRules = (@{
    AllowedHeaders=@("x-ms-blob-content-type","x-ms-blob-content-disposition");
    AllowedOrigins=@("*");
    MaxAgeInSeconds=30;
    AllowedMethods=@("Get","Connect")},
    @{
    AllowedOrigins=@("http://www.fabrikam.com","http://www.contoso.com"); 
    ExposedHeaders=@("x-ms-meta-data*","x-ms-meta-customheader"); 
    AllowedHeaders=@("x-ms-meta-target*","x-ms-meta-customheader");
    MaxAgeInSeconds=30;
    AllowedMethods=@("Put")})

Set-AzStorageCORSRule -ServiceType Blob -CorsRules $CorsRules

$ContinueOnFailure = $true # Set it to $false if want to terminate the operation quickly on encountering failures $token = $null $TotalDirectoriesSuccess = 0 $TotalFilesSuccess = 0 $totalFailure = 0 $FailedEntries = New-Object System.Collections.Generic.List[System.Object] do {
if ($ContinueOnFailure) { $result = Update-AzDataLakeGen2AclRecursive -FileSystem "filesystem1" -Path "dir1" -Acl $acl -BatchSize 100 -MaxBatchCount 50 -ContinuationToken $token -Context $ctx -ContinueOnFailure } else { $result = Update-AzDataLakeGen2AclRecursive -FileSystem "filesystem1" -Path "dir1" -Acl $acl -BatchSize 100 -MaxBatchCount 50 -ContinuationToken $token -Context $ctx }
# echo $result $TotalFilesSuccess += $result.TotalFilesSuccessfulCount $TotalDirectoriesSuccess += $result.TotalDirectoriesSuccessfulCount $totalFailure += $result.TotalFailureCount $FailedEntries += $result.FailedEntries $token = $result.ContinuationToken }while (($null -ne $token) -and (($ContinueOnFailure) -or ($result.TotalFailureCount -eq 0))) echo "" echo "[Result Summary]" echo "TotalDirectoriesSuccessfulCount: `t$($TotalDirectoriesSuccess)" echo "TotalFilesSuccessfulCount: `t`t`t$($TotalFilesSuccess)" echo "TotalFailureCount: `t`t`t`t`t$($totalFailure)" echo "ContinuationToken: `t`t`t`t`t$($token)" echo "FailedEntries:"$($FailedEntries | ft)
